import Link from "next/link";
import MasterPage from "../components/Master/Master";
import fetch from "isomorphic-unfetch";
import { bitcoinUrlBase, bitcoinApiKey } from "../config/config";

const Index = props => (
  <MasterPage>
    <div className="row">
      <div className="col-12">
        <h2>Precio del bitcoin</h2>
        {console.log(props)}
      </div>
      <div className="col-md-8">
        <h2>Noticias sobre bitcoin</h2>
      </div>
      <div className="col-md-4">
        <h2>Proximos eventos bitcoin</h2>
      </div>
    </div>
  </MasterPage>
);

Index.getInitialProps = async () => {
  const url = `${bitcoinUrlBase}v1/cryptocurrency/listings/latest?start=1&limit=1&convert=ARS`;

  const response = await fetch(url, {
    headers: {
      "X-CMC_PRO_API_KEY": bitcoinApiKey
    }
  });

  const data = await response.json();

  return {priceBitcoinInArs: data.data[0].quote.ars};
};

export default Index;
