import Link from 'next/link';
import MasterPage from '../components/Master/Master';
import fetch from 'isomorphic-unfetch';
import {bitcoinUrlBase} from '../config/config';
import { apiKey } from '../../cotizador-criptomonedas-app/src/configuration/config';

const Index = () =>(
    <MasterPage>
        <div className='row'>
            <div className='col-12'>
                <h2>Precio del bitcoin</h2>

            </div>
            <div className='col-md-8'>
                <h2>Noticias sobre bitcoin</h2>

            </div>
            <div className='col-md-4'>
                <h2>Proximos eventos bitcoin</h2>

            </div>
        </div>

    </MasterPage>
    
);

Index.getInitialProps = async () =>{
    const url = `${bitcoinUrlBase}v1/cryptocurrency/price-performance-stats/latest`;
    console.log(url);
    
    await fetch(url, {headers: 
        {
            'X-CMC_PRO_API_KEY': apiKey
        }})
        .then(resp =>{
            console.log(resp);
        });
}

export default Index;
